/* AK7709 CODEC Private Data */
struct ak7709_priv {
	struct device *dev;

	enum snd_soc_control_type control_type;
	struct snd_soc_component *component;
	struct spi_device *spi;
	struct i2c_client *i2c;
	struct regmap *regmap;

	u16 device_id;

	int fs;

	int pdn_gpio;
	int tdmmode[AIF_PORT_NUM];
	int pll_ckm;
	int ckramsel;
	int ckramdiv;
	int sram_start;

	// int exbck[NUM_SDIO];

	int master[NUM_SYNCDOMAIN];
	int sdcks[NUM_SYNCDOMAIN];

	int pllinput;   // REFSELbit-1
					// 0:XTI, 1:BICKA, 2:BICKB, 3:BICKC, 4:BICKD
					// 5:BICKE, 6:BICKF, 7:BICKG, 8:BICKH
	int xtifs;      // 0 : 12.288MHz,  1: 18.432MHz,	 2 : 24.576MHz
	int sdfs[NUM_SYNCDOMAIN];	// 0:8kHz, 1:12kHz, 2:16kHz, 3:24kHz
								// 4:32kHz, 5:48kHz, 6:96kHz, 7:192kHz
								// 8;384kHz, 9:768kHz
	int sdbick[NUM_SYNCDOMAIN];	// 0:64fs, 1:48fs, 2:32fs, 3:128fs, 4:256fs,
								// 5:512fs, 6:1024fs

	int hifi_ram;
	int ak77dsp_ram;

	unsigned int wordlen_bit[AIF_PORT_NUM];  // DIDLx/DODLx[1:0] bits
	unsigned int audio_format[AIF_PORT_NUM];
	unsigned int slotlen_bit[AIF_PORT_NUM];   // DISLx/DOSLx[1:0] bits

	struct delayed_work fw_work;
};

#ifdef AK7709_IO_CONTROL
struct _ak7709_pd_handler {
	int ref_count;
	struct mutex lock;
	struct ak7709_priv *data;
} ak7709_pd_handler = {
	.ref_count = -1,
	.data = NULL,
};
#endif

