static int get_chipID(struct snd_kcontrol *kcontrol,
			struct snd_ctl_elem_value  *ucontrol)
{
	struct snd_soc_component *component = snd_soc_kcontrol_component(kcontrol);
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	int i;
	unsigned int value;

	struct {
		unsigned int val;
		const int def;
	} devid[] = {
		{0, 0x09},
		{0, 0x77},
	};

	for (i = 0; i < ARRAY_SIZE(devid); i++)
		devid[i].val = snd_soc_component_read(component, AK7709_C0_400_DEVICE_ID1 + i);

	value = (u16)devid[1].val + ((u16)devid[0].val << 8);

	dev_info(ak7709->dev, "ak7709 chipID(0x%04x) %s\n", value, __func__);

	ucontrol->value.enumerated.item[0] = value;

	return 0;
}

static int setPLLOut(
struct snd_soc_component *component)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	unsigned int pln;
	unsigned int addr;
	int nPLLInFs;
	int nBfs, fs;
	int n, nMax;

	if (ak7709->pllinput >= ARRAY_SIZE(pllinput_texts)) {
		dev_info(ak7709->dev, "%s Invalid PLL Input! (%d)\n",
			__func__, ak7709->pllinput);
		return(-EINVAL);
	}

	if (ak7709->pllinput == 0) {//XTI
		nPLLInFs = XtiFsTab[ak7709->xtifs];
	} else {
		nBfs = sdbicktab[ak7709->sdbick[ak7709->pllinput-1]];
		fs = sdfstab[ak7709->sdfs[ak7709->pllinput-1]];
		dev_info(ak7709->dev, "%s nBfs=%d fs=%d\n", __func__, nBfs, fs);
		nPLLInFs = nBfs * fs;
	}

	n = 0;
	nMax = ARRAY_SIZE(PLLInFsTab) / sizeof(PLLInFsTab[0]);

	do {
		dev_info(ak7709->dev, "%s n=%d PLL:%d %d\n",
			__func__, n, nPLLInFs, PLLInFsTab[n]);
		if (nPLLInFs == PLLInFsTab[n])
			break;
		n++;
	} while (n < nMax);


	if (n == nMax) {
		dev_info(ak7709->dev, "%s: PLL1 setting Error!\n",  __func__);
		return(-EINVAL);
	}

	addr = AK7709_C0_002_SYSTEM_CLOCK_SET3;
	snd_soc_component_update_bits(component,
				addr++, 0xFF, ak7709_refsel[n].plmBits);
	pln = (unsigned int)ak7709_refsel[n].plnBits;
	snd_soc_component_update_bits(component,
				addr++, 0xFF, (0xFF & pln));
	snd_soc_component_update_bits(component,
				addr++, 0xFF, (0xFF & (pln>>8)));

	return 0;
}


static int setSDClock(struct snd_soc_component *component, int nSDNo)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int addr;
	int bickNo;
	u32 fs, bickfs;
	u32 cksfs, cksbickfs;
	u32 sdv, bdv;

	if (nSDNo >= NUM_SYNCDOMAIN)
		return -EINVAL;

	if (ak7709->sdcks[nSDNo] >= ARRAY_SIZE(sd_cks_texts))
		return -EINVAL;


	addr = AK7709_C0_010_SYNC_DOMAIN1_SET1 + (4 * nSDNo);
	snd_soc_component_update_bits(component,
		addr, 0x80, (ak7709->master[nSDNo]<<7));
	snd_soc_component_update_bits(component,
		(addr+1), 0x0F, ak7709->sdcks[nSDNo]);

	if (ak7709->master[nSDNo] == 0) {
		if (nSDNo == (ak7709->pllinput-1)) {	// // 0:XTI, 1~:BICKx
			setPLLOut(component);
		}
		return 0;
	}

	if (ak7709->sdcks[nSDNo] == 0)
		return 0;

	fs = sdfstab[ak7709->sdfs[nSDNo]];
	bickfs = sdbicktab[ak7709->sdbick[nSDNo]] * fs;

	if (ak7709->sdcks[nSDNo] == 2) {//XTI
		bdv = XtiFsTab[ak7709->xtifs] / bickfs;
	} else if (ak7709->sdcks[nSDNo] == 1) { //SDGCLK
		bdv = SDGCLK_FREQ / bickfs;
	} else { // BCLK
		bickNo = ak7709->sdcks[nSDNo] - 3;
		cksfs = sdfstab[ak7709->sdfs[bickNo]];
		cksbickfs = sdbicktab[ak7709->sdbick[bickNo]];
		bdv = (cksbickfs * cksfs) / bickfs;
	}

	sdv = ak7709->sdbick[nSDNo];
	if (sdv > 4)
		sdv += 2;	// fs>=512

	dev_info(ak7709->dev, "%s, fs = %d BICKfs = %d BDV=%d, SDV=%d\n",
		__func__, fs, bickfs, bdv, sdbicktab[sdv]);
	bdv--;
	if (bdv > 255) {
		dev_info(ak7709->dev, "%s: BDV Error! SD No = %d, bdv bit = %d\n",
			__func__, nSDNo, bdv);
		bdv = 255;
	}

	snd_soc_component_update_bits(component,
		(addr+2), 0xFF, bdv); //BDIV[7:0]
	snd_soc_component_update_bits(component,
		(addr+3), 0xFF, sdv);

	return 0;
}

static int start_sram(struct snd_soc_component *component,
	int sram_start, int ckramsel, int ckramdiv)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	dev_info(ak7709->dev, "sram_start(%d) ckramsel(%d) ckramdiv((0x%02x))%s(%d)\n",
		sram_start, ckramsel, ckramdiv, __func__, __LINE__);

	if (sram_start == 1) {
		snd_soc_component_update_bits(component,
			AK7709_C0_006_SRAM_INIT_SET1, 0x0F, ckramsel);
		snd_soc_component_update_bits(component,
			AK7709_C0_007_SRAM_INIT_SET2, 0xFF, ckramdiv);
	}
	snd_soc_component_update_bits(component,
		AK7709_C0_008_SRAM_INIT_SET3, 0x01, sram_start);

	return 0;
}

