static int get_sdio1_slot(struct snd_kcontrol *kcontrol,
			struct snd_ctl_elem_value  *ucontrol)
{
	struct snd_soc_component *component = snd_soc_kcontrol_component(kcontrol);
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	ucontrol->value.enumerated.item[0] = ak7709->slotlen_bit[0];

	return 0;
}

static int set_sdio1_slot(struct snd_kcontrol *kcontrol,
			struct snd_ctl_elem_value  *ucontrol)
{
	struct snd_soc_component *component = snd_soc_kcontrol_component(kcontrol);
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int    currMode = ucontrol->value.enumerated.item[0];

	if (currMode < (ARRAY_SIZE(data_width_texts))) {
		ak7709->slotlen_bit[0] = currMode;
	} else {
		dev_info(ak7709->dev, "%s Invalid Value selected!\n", __func__);
		return -EINVAL;
	}
	return 0;
}

