static int get_cram_offset_77dsp1(struct snd_kcontrol *kcontrol,
			       struct snd_ctl_elem_value *ucontrol)
{
	struct snd_soc_component *component = snd_soc_kcontrol_component(kcontrol);
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	unsigned int upper_value;
	unsigned int lower_value;
	unsigned int value;

	upper_value =
		snd_soc_component_read(component,
			AK7709_C1_133_77DSP1_CRAM_OFFSET_SET2);
	lower_value =
		snd_soc_component_read(component,
			AK7709_C1_132_77DSP1_CRAM_OFFSET_SET1);

	value = (((0xFF & upper_value) << 8) | (0xFF & lower_value));

	dev_info(ak7709->dev, "77DSP1 CRAM Offset(0x%04x) %s()\n", value, __func__);

	ucontrol->value.integer.value[0] = value;

	return 0;
}

