static bool ak7709_volatile(struct device *dev, unsigned int reg)
{
	bool ret;

	// Readonly
	if ((reg >= AK7709_C0_390_MASTER_SPI_REC_START_ADDR1) &&
		(reg <= AK7709_C0_473_MASTER_SPI_STS3)) {
		ret = true;
	} else if ((reg >= AK7709_C1_021_HIFI4A_GPO1) &&
			(reg <= AK7709_C1_024_HIFI4A_GPO4)) {
		ret = true;
	} else if ((reg >= AK7709_C1_051_HIFI4B_GPO1) &&
			(reg <= AK7709_C1_054_HIFI4B_GPO4)) {
		ret = true;
	} else if ((reg == AK7709_C1_063_HIFI4_WDT_ERR) ||
			(reg == AK7709_C1_C4F_UART_USR) ||
			(reg == AK7709_C1_093_UART_SPI)) {
		ret = true;
	} else if ((reg >= AK7709_C1_10A_77DSP_GPO_STS) &&
			(reg <= AK7709_C1_10B_77DSP_WDT_ERR)) {
		ret = true;
	} else if ((reg >= AK7709_C2_010_SRCA_STS1) &&
			(reg <= AK7709_C2_011_SRCA_STS2)) {
		ret = true;
	} else if (reg == AK7709_C2_110_SRCB_STS1) {
		ret = true;
	} else if ((reg >= AK7709_C2_306_DIR_RECEIVER_STS1) &&
			(reg <= AK7709_C2_31F_DIR_Q_SUBCODE_BUF10)) {
		ret = true;
	} else if (reg == AK7709_C2_326_DIR_STC_DAT_DET) {
		ret = true;
	} else {
#ifdef AK7709_DEBUG
		if (reg > AK7709_C2_32E_DMIC2_RCH_VOL)
			ret = false;
		else
			ret = true;
#else
		ret = false;
#endif
	}

	return ret;
}

static bool ak7709_readable(struct device *dev, unsigned int reg)
{
	bool ret;
	int  n, ne;
	int  tabnum;

	tabnum = ARRAY_SIZE(ak7709_reg);

	ne = 0x0;
	do {
		n = ne;
		ne += 0x10;
		if (ne >= tabnum) {
			ne = tabnum;
			break;
		}
	} while (reg >= ak7709_reg[ne].reg);

	ret = false;

	do {
		if (ak7709_reg[n].reg == reg) {
			ret = true;
			break;
		}
		n++;
	} while (n < ne);

	return ret;
}

static bool ak7709_writeable(struct device *dev, unsigned int reg)
{
	bool ret;

#ifndef AK7709_DEBUG
	ret = ak7709_volatile(NULL, reg);
	if (ret)
		return false;
#endif

	ret = ak7709_readable(dev, reg);
	return ret;
}

#ifdef AK7709_I2C_IF
static int ak7709_i2c_read(
struct i2c_client *client,
u8 *reg,
int reglen,
u8 *data,
int datalen)
{
	struct i2c_msg xfer[2];
	int ret;

	/* Write register */
	xfer[0].addr = client->addr;
	xfer[0].flags = 0;
	xfer[0].len = reglen;
	xfer[0].buf = reg;

	/* Read data */
	xfer[1].addr = client->addr;
	xfer[1].flags = I2C_M_RD;
	xfer[1].len = datalen;
	xfer[1].buf = data;

	ret = i2c_transfer(client->adapter, xfer, 2);

	if (ret == 2)
		return 0;
	else
		return ret;
}
#endif

static int ak7709_reads(struct snd_soc_component *component, u8 *tx,
			size_t wlen, u8 *rx, size_t rlen)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int ret;

	akdbgprt("%s tx[0]=0x%02x, tx[1]=0x%02x, raddr=0x%02x%02x, wlen=%d, rlen=%d\n",
		__func__, tx[0], tx[1], tx[2], tx[3], (int)wlen, (int)rlen);

#ifdef AK7709_I2C_IF
	ret = ak7709_i2c_read(ak7709->i2c, tx, wlen, rx, rlen);
#else
	ret = spi_write_then_read(ak7709->spi, tx, wlen, rx, rlen);
#endif

	return ret;

}

static int ak7709_writes(struct snd_soc_component *component,
			const u8 *tx, size_t wlen)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int rc;

	akdbgprt("%s tx[0]=%02x tx[1]=%02x tx[2]=%02x, wlen=%d\n",
		__func__, (int)tx[0], (int)tx[1], (int)tx[2], (int)wlen);

#ifdef AK7709_I2C_IF
	rc = i2c_master_send(ak7709->i2c, tx, wlen);
#else
	rc = spi_write(ak7709->spi, tx, wlen);
#endif

	return rc;
}

static int crc_read(struct snd_soc_component *component)
{
	int rc;
	u8 tx[5], rx[2];

	tx[0] = CTRLIF_FORMAT_NORMAL_2BYTE;
	tx[1] = COMMAND_CRC_READ;
	tx[2] = 0;
	tx[3] = 0;
	tx[4] = 0;

	rc = ak7709_reads(component, tx, 5, rx, 2);

	return (rc < 0) ? rc : ((rx[0] << 8) + rx[1]);
}

static int calc_CRC(int length, u8 *data)
{

#define CRC16_CCITT (0x1021)

	unsigned short crc = 0x0000;
	int i, j;

	for (i = 0; i < length; i++) {
		crc ^= *data++ << 8;
		for (j = 0; j < 8; j++) {
			if (crc & 0x8000) {
				crc <<= 1;
				crc ^= CRC16_CCITT;
			} else {
				crc <<= 1;
			}
		}
	}

	pr_info("%s CRC=%x\n", __func__, crc);

	return crc;
}

static int ak7709_ram_download(struct snd_soc_component *component,
				const u8 *tx_ram, u64 num, u16 crc)
{
	int rc;
	u16 read_crc;

	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	rc = ak7709_writes(component, tx_ram, num);
	if (rc < 0) {
		dev_info(ak7709->dev, "%s ak7709_writes Error! rc=%d\n",
			__func__, rc);
		return rc;
	}

	if ((crc != 0) && (rc >= 0))  {
		read_crc = crc_read(component);
		if (read_crc == crc) {
			rc = 0;
		} else {
			dev_info(ak7709->dev,  "%s fail!!!!!!!!\n", __func__);
			rc = 1;
		}
	}

	return rc;

}

static int ak7709_write_ram(
	struct snd_soc_component *component,
	u8 *upRam,
	unsigned int nWSize)
{
	int n, ret;
	int	wCRC;
	unsigned int devByte;
	unsigned int devLen;
	unsigned int restLen;
	unsigned int commandLen;
	unsigned int ckResetReg;
	u8 *ramdata;

	if ((upRam[1] & 0x80) == 0) {
		switch (upRam[0]) {
		case CTRLIF_FORMAT_NORMAL_4BYTE:
			commandLen = HIFI_RAM_COMMAND_LENGTH;
			break;
		case CTRLIF_FORMAT_NORMAL_2BYTE:
			commandLen = AK77DSP_RAM_COMMAND_LENGTH;
			break;
		}
		devByte = ((unsigned int)upRam[2] << 8) + (unsigned int)upRam[3];
		devLen = commandLen + devByte;
	} else {
		devByte = 0;
		devLen = nWSize;
	}

	if (upRam[0] == CTRLIF_FORMAT_NORMAL_2BYTE) {
		snd_soc_component_update_bits(component,
			AK7709_C0_11FA_CONTROL, 0xFF, 0x55);
		snd_soc_component_update_bits(component,
			AK7709_C1_361_77DSPCTRL, 0xFF, 0x43);
	}

	ckResetReg = snd_soc_component_read(component,
						AK7709_C0_000_SYSTEM_CLOCK_SET1);
	if ((ckResetReg & CKRESETN_MASK) == 0) {
		snd_soc_component_update_bits(component,
			AK7709_C0_000_SYSTEM_CLOCK_SET1, CKRESETN_MASK, CKRESETN_MASK);
		mdelay(10);
	}

	ramdata = upRam;
	restLen = nWSize;

	if (devByte != 0) {
		ramdata += AK77DSP_FIRMWARE_INDEX_LENGTH;
		restLen -= AK77DSP_FIRMWARE_INDEX_LENGTH;
	}

	ret = 0;

	do {
		if (restLen < devLen)
			devLen = restLen;
		wCRC = calc_CRC((devLen-1), &ramdata[1]);
		n = MAX_LOOP_TIMES;
		do {
			ret = ak7709_ram_download(component, ramdata, devLen, wCRC);
			if (ret == 0)
				break;
			n--;
		} while (n > 0);

		if (ret < 0) {
			ret = -EINVAL;
			break;
		}
		restLen -= devLen;
		if (restLen > 0)
			ramdata += devLen;
	} while (restLen > 0);

	if (upRam[0] == CTRLIF_FORMAT_NORMAL_2BYTE) {
		snd_soc_component_update_bits(component,
			AK7709_C1_361_77DSPCTRL, 0xFF, 0x42);
		snd_soc_component_update_bits(component,
			AK7709_C0_11FA_CONTROL, 0xFF, 0x00);
	}

	if ((ckResetReg & CKRESETN_MASK) == 0) {
		snd_soc_component_update_bits(component,
			AK7709_C0_000_SYSTEM_CLOCK_SET1, 0xFF, ckResetReg);
	}

	return ret;
}

static int ak7709_write_hifi_ram(
	struct snd_soc_component *component,
	u8 *upRam,
	unsigned int nWSize)
{
	int ret;
	unsigned int value;
	unsigned int mask;
	unsigned int writeCommand;

	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	value = snd_soc_component_read(component,
					AK7709_C1_001_HIFI4_RESET_CTL);
	if (value > 0xFF) {
		dev_info(ak7709->dev, "%s(%d), Read Error!\n",  __func__, __LINE__);
		return -EIO;
	}

	writeCommand = (0x80 | upRam[1]);
	switch (writeCommand) {
	case HIFI4A_COMMAND_WRITE_RAM:
		mask =  D11RESETNBIT_MASK;
		break;
	case HIFI4B_COMMAND_WRITE_RAM:
		mask =  D12RESETNBIT_MASK;
		break;
	default:
		return -EINVAL;
	}

	if ((value & mask) == mask) {
		dev_info(ak7709->dev, "%s(%d), DSP is running!\n",  __func__, __LINE__);
		return -EINVAL;
	}

	ret = ak7709_write_ram(component, upRam, nWSize);

	return ret;

}

static int ak7709_hifi_firmware_write(
				struct snd_soc_component *component, u16 sel)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int ret = 0;
	unsigned int nRamSize;
	u8  *ram_basic;
	const struct firmware *fw;
	u8  *fwdn;
	char szFileName[32];

	dev_info(ak7709->dev, "%s sel=%d\n", __func__, sel);

	if (sel >= ARRAY_SIZE(ak7709_hifi_firmware)) {
		dev_info(ak7709->dev, "%s: invalid command %d\n",  __func__, sel);
		return(-EINVAL);
	}

	if (sel == 0) {
		dev_info(ak7709->dev, "%s: invalid command %d\n",  __func__, sel);
		return 0;
	} else if (sel <= ARRAY_SIZE(hifiBasicRam)) {
		ram_basic = hifiBasicRam[sel-1].ramdata;
		nRamSize  = hifiBasicRam[sel-1].length;
		ret = ak7709_write_hifi_ram(component, ram_basic, nRamSize);
	} else {
		sprintf(szFileName, "ak7709_%s.bin", ak7709_hifi_firmware[sel]);

		ret = request_firmware(&fw, szFileName, ak7709->component->dev);
		if (ret) {
			dev_info(ak7709->dev, "%s: could not load firmware=%d\n",
				szFileName, ret);
			return -EINVAL;
		}

		fwdn = kmalloc((unsigned long)fw->size, GFP_KERNEL);
		if (fwdn == NULL) {
			release_firmware(fw);
			return -ENOMEM;
		}

		memcpy((void *)fwdn, fw->data, fw->size);

		ret = ak7709_write_hifi_ram(component, (u8 *)fwdn, (fw->size));

		kfree(fwdn);
		release_firmware(fw);
	}

	return ret;
}

