static int ak7709_write_77dsp_ram(
	struct snd_soc_component *component,
	u8 *upRam,
	unsigned int nWSize)
{
	int ret;
	unsigned int value;
	unsigned int mask;
	u8 command;

	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	value = snd_soc_component_read(component,
					AK7709_C1_100_77DSP_RESET_CTL);
	if (value > 0xFF) {
		dev_info(ak7709->dev, "%s(%d), Read Error!\n",  __func__, __LINE__);
		return -EIO;
	}

	command = (0x80 | upRam[1]);

	switch (command) {
	case AK77DSP1_PRAM_COMMAND_WRITE:
	case AK77DSP1_CRAM_COMMAND_WRITE:
	case AK77DSP1_OFREG_COMMAND_WRITE:
		mask =  D21RESETNBIT_MASK;
		break;
	case AK77DSP2_PRAM_COMMAND_WRITE:
	case AK77DSP2_CRAM_COMMAND_WRITE:
	case AK77DSP2_OFREG_COMMAND_WRITE:
		mask =  D22RESETNBIT_MASK;
		break;
	case AK77DSP3_PRAM_COMMAND_WRITE:
	case AK77DSP3_CRAM_COMMAND_WRITE:
	case AK77DSP3_OFREG_COMMAND_WRITE:
		mask =  D23RESETNBIT_MASK;
		break;
	case AK77DSP4_PRAM_COMMAND_WRITE:
	case AK77DSP4_CRAM_COMMAND_WRITE:
	case AK77DSP4_OFREG_COMMAND_WRITE:
		mask =  D24RESETNBIT_MASK;
		break;
	default:
		dev_info(ak7709->dev, "%s, command 0x%x error!\n",  __func__, command);
		return -EINVAL;
	}

	if ((value & mask) == mask) {
		dev_info(ak7709->dev, "%s(%d), DSP is running!\n",  __func__, __LINE__);
		return -EINVAL;
	}

	ret = ak7709_write_ram(component, upRam, nWSize);

	return ret;

}

static int ak7709_77dsp_firmware_write(
				struct snd_soc_component *component, u16 sel)
{
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	int ret = 0;
	int nRamSize;
	u8  *ram_basic;
	const struct firmware *fw;
	u8  *fwdn;
	char szFileName[32];

	if (sel >= ARRAY_SIZE(ak7709_77dsp_firmware)) {
		dev_info(ak7709->dev, "%s: invalid command %d\n",  __func__, sel);
		return(-EINVAL);
	}

	if (sel == 0) {
		dev_info(ak7709->dev, "%s: invalid command %d\n",  __func__, sel);
		return 0;
	} else if (sel <= ARRAY_SIZE(ak77dspBasicRam)) {
		ram_basic = ak77dspBasicRam[sel-1].ramdata;
		nRamSize  = ak77dspBasicRam[sel-1].length;
		ret = ak7709_write_77dsp_ram(component, ram_basic, nRamSize);
	} else {
		sprintf(szFileName, "ak7709_%s.bin", ak7709_77dsp_firmware[sel]);

		ret = request_firmware(&fw, szFileName, ak7709->component->dev);
		if (ret) {
			dev_info(ak7709->dev, "%s could not load firmware=%d\n",
				szFileName, ret);
			return -EINVAL;
		}

		dev_info(ak7709->dev, "%s name=%s size=%d\n",
			__func__, szFileName, (int)fw->size);

		fwdn = kmalloc((unsigned long)fw->size, GFP_KERNEL);
		if (fwdn == NULL) {
			release_firmware(fw);
			return -ENOMEM;
		}

		memcpy((void *)fwdn, fw->data, fw->size);

		ret = ak7709_write_77dsp_ram(component, (u8 *)fwdn, (fw->size));

		kfree(fwdn);
		release_firmware(fw);
	}

	return ret;
}

