static int set_test_reg(
struct snd_kcontrol	   *kcontrol,
struct snd_ctl_elem_value  *ucontrol)
{
	struct snd_soc_component *component = snd_soc_kcontrol_component(kcontrol);
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);
	u32	currMode = ucontrol->value.enumerated.item[0];
	int	i, value, ret;
	int	   regs, rege;
	struct device *dev;

	nTestRegNo = currMode;

	if (currMode < (ARRAY_SIZE(test_reg_select))) {
		if (currMode == 0) {
			regs = AK7709_C0_000_SYSTEM_CLOCK_SET1;
			rege = AK7709_C0_473_MASTER_SPI_STS3;
		} else if (currMode == 1) {
			regs = AK7709_C1_000_HIFI4_CLK_MASK;
			rege = AK7709_C1_0A0_MASTER_I2C_POWERMNG;
		} else if (currMode == 2) {
			regs = AK7709_C1_100_77DSP_RESET_CTL;
			rege = AK7709_C1_C4F_UART_USR;
		} else if (currMode == 3) {
			regs = AK7709_C2_000_SRCA_POWERMNG;
			rege = AK7709_C2_32E_DMIC2_RCH_VOL;
		} else {
			regs = AK7709_C0_400_DEVICE_ID1;
			rege = AK7709_C0_401_DEVICE_ID2;
		}

#ifdef AK7709_I2C_IF
		dev = &(ak7709->i2c->dev);
#else
		dev = &(ak7709->spi->dev);
#endif

		for (i = regs; i <= rege; i++) {
			ret = ak7709_readable(dev, i);
			if (ret) {
				value = snd_soc_component_read(component, i);
				pr_info("***AK7709 Addr,Reg=(%03x, %02x)\n", i, value);
				if (ret < 0) {
					pr_info("%s(), ret=%d\n", __func__, ret);
					return ret;
				}
			}

		}
	} else if (currMode >= 4) {
		ret = test_read_ram(kcontrol, currMode);
	}

	return 0;
}
