static int ak7709_set_dai_fmt(struct snd_soc_dai *dai, unsigned int fmt)
{
	struct snd_soc_component *component = dai->component;
	struct ak7709_priv *ak7709 = snd_soc_component_get_drvdata(component);

	int i, msnbit;
	unsigned int portNo;
	unsigned int addr, mask, value;
	unsigned int format, diolsb, dioedge, dioslot;
	int portLink[NUM_EXT_PORT_NUM];
	int extPortNo;

	dev_info(ak7709->dev, "%s(), format(0x%02x), dai(%d)\n", __func__, fmt, dai->id);

	if ((dai->id < AIF_PORT1) && (dai->id >= AIF_PORT_NUM))
		return (-EINVAL);

	portNo = dai->id - AIF_PORT1;

	/* set master/slave audio interface */
	switch (fmt & SND_SOC_DAIFMT_MASTER_MASK) {
	case SND_SOC_DAIFMT_CBS_CFS:
		msnbit = AK7709_MSN_SLAVE;
		ak7709->master[portNo] = 0;
		break;
	case SND_SOC_DAIFMT_CBM_CFM:
		msnbit = AK7709_MSN_MASTER;
		ak7709->master[portNo] = 1;
		break;
	case SND_SOC_DAIFMT_CBS_CFM:
	case SND_SOC_DAIFMT_CBM_CFS:
	default:
		dev_info(ak7709->dev, "%s(), Clock mode unsupported\n", __func__);
		return -EINVAL;
	}

	addr = AK7709_C0_010_SYNC_DOMAIN1_SET1 + 4*portNo;
	snd_soc_component_update_bits(component, addr, AK7709_MSN_MASK, msnbit);
	if (msnbit == AK7709_MSN_MASTER) {
		setSDClock(component, portNo);
	} else {
		if (portNo == (ak7709->pllinput-1))
			setPLLOut(component);
	}

	ak7709->audio_format[portNo] = (fmt & SND_SOC_DAIFMT_FORMAT_MASK);
	mask = 0xB8;
	switch (fmt & SND_SOC_DAIFMT_FORMAT_MASK) {
	case SND_SOC_DAIFMT_I2S:
		format = AK7709_LRIF_I2S_MODE;
		diolsb = 0;
		if (ak7709->tdmmode[portNo] == 0) {
			dioedge = 0;
			dioslot = ak7709->wordlen_bit[portNo];
		} else {
			dioedge = 1;
			dioslot = 3;
		}
		break;
	case SND_SOC_DAIFMT_LEFT_J:
		format = AK7709_LRIF_MSB_MODE;
		diolsb = 0;
		if (ak7709->tdmmode[portNo] == 0) {
			dioedge = 0;
			dioslot = ak7709->wordlen_bit[portNo];
		} else {
			dioedge = 1;
			dioslot = 3;
		}
		break;
	case SND_SOC_DAIFMT_RIGHT_J:
		format = AK7709_LRIF_LSB_MODE;
		diolsb = 1;
		if (ak7709->tdmmode[portNo] == 0) {
			dioedge = 0;
			dioslot = ak7709->wordlen_bit[portNo];
		} else {
			dioedge = 1;
			dioslot = 3;
		}
		break;
	case SND_SOC_DAIFMT_DSP_A: //PCM Short
		format = AK7709_LRIF_PCM_SHORT_MODE;
		diolsb = 0;
		dioedge = 1;
		dioslot = ak7709->slotlen_bit[portNo];
		break;
	case SND_SOC_DAIFMT_DSP_B: //PCM Long
		format = AK7709_LRIF_PCM_LONG_MODE;
		diolsb = 0;
		dioedge = 1;
		dioslot = ak7709->slotlen_bit[portNo];
		break;
	default:
		dev_info(ak7709->dev, "%s invaliad param\n", __func__);
		return -EINVAL;
	}

	/* set format */
	value = format;
	/* lrcka/bicka */
	snd_soc_component_update_bits(component, AK7709_C0_040_BICK_FMT_SET1, AK7709_LRIF_MASK, value);

	/* set SDIO format */
	/* set Slot length */
	addr = AK7709_C0_050_SDIN1_DIGITAL_IN_FMT + 2*portNo;
	value = (dioedge <<  7) + (dioslot <<  4) + (diolsb <<  3);
	snd_soc_component_update_bits(component, addr, mask, value);
	addr = AK7709_C0_066_SDOUT1_DIGITAL_OUT_FMT + 2*portNo;
	snd_soc_component_update_bits(component, addr, mask, value);

	get_extportinf(portLink);
	for (i = 0; i < NUM_EXT_PORT_NUM; i++) {
		if (portLink[i] == portNo) {
			extPortNo = AIF_PORT_NUM + i;
			value = (dioedge <<  7) + (dioslot <<  4) + (diolsb <<  3);
			addr = AK7709_C0_050_SDIN1_DIGITAL_IN_FMT + 2*extPortNo;
			snd_soc_component_update_bits(component, addr, mask, value);
			addr = AK7709_C0_066_SDOUT1_DIGITAL_OUT_FMT + 2*extPortNo;
			snd_soc_component_update_bits(component, addr, mask, value);
		}
	}

	return 0;
}

