static int ak7709_spi_probe(struct spi_device *spi)
{
	struct ak7709_priv *ak7709;
	int i, ret;
	struct {
		unsigned int val;
		const int def;
	} devid[] = {
		{0, 0x09},
		{0, 0x77},
	};

	ak7709 = devm_kzalloc(&spi->dev, sizeof(struct ak7709_priv),
				GFP_KERNEL);
	if (ak7709 == NULL)
		return -ENOMEM;

	ak7709->dev = &spi->dev;
	ak7709->regmap = devm_regmap_init(&spi->dev, NULL, &spi->dev,
						&ak7709_regmap);

	if (IS_ERR(ak7709->regmap)) {
		ret = PTR_ERR(ak7709->regmap);
		dev_info(&spi->dev, "%s() Failed to allocate register map: %d\n", __func__, ret);
		return ret;
	}

	spi_set_drvdata(spi, ak7709);

	ak7709->control_type = SND_SOC_SPI;
	ak7709->spi = spi;

	ret = ak7709_parse_dt(ak7709);
	if (ret) {
		dev_info(ak7709->dev, "%s() gpio parse dts failed\n", __func__);
		ak7709->pdn_gpio = -1;
		// return ret;
	}

	if (ak7709->pdn_gpio > 0) {
		ret = gpio_request(ak7709->pdn_gpio, "ak7709 pdn");
		if (ret) {
			dev_info(ak7709->dev, "%s() gpio request failed, ret:%d\n", __func__, ret);
			// return ret;
		}
	}
	/* power up*/
	ak7709_ext_reset(ak7709);
	ret = ak7709EnableCtrlIF(ak7709);
	if (ret) {
		dev_info(ak7709->dev, "%s() ak7709 enable ctrl interface failed: %d\n", __func__, ret);
		return ret;
	}
	/* read ak7709 chipid */
	for (i = 0; i < ARRAY_SIZE(devid); i++) {
		ret = regmap_read(ak7709->regmap, AK7709_C0_400_DEVICE_ID1 + i, &devid[i].val);
		if (ret < 0 || devid[i].val > 0xFF) {
			dev_info(ak7709->dev, "%s() Read ID Error!\n", __func__);
			return -EIO;
		}

		if (devid[i].val != devid[i].def) {
			dev_info(ak7709->dev, "%s() This is not AK7709 dev", __func__);
			return -EIO;
		}
	}

	for (i = 0 ; i < AIF_PORT_NUM ; i++)
		ak7709->tdmmode[i] = TDM_MODE_TDM;

	ret = devm_snd_soc_register_component(&spi->dev,
						&soc_codec_dev_ak7709,
						&ak7709_dai[0],
						ARRAY_SIZE(ak7709_dai));
	if (ret != 0) {
		dev_info(&spi->dev, "%s() Failed to register CODEC: %d\n", __func__, ret);
		return ret;
	}

	mtk_spk_set_type(MTK_SPK_AKM_AK7709);

#if !IS_ENABLED(CONFIG_DEVICE_MODULES_SND_SOC_MT6991)
	INIT_DELAYED_WORK(&ak7709->fw_work, ak7709_fw_work);
#endif

	return 0;
}

